package com.example.demo.LeetCode;

import org.junit.Test;

public class FindTheDifference {
    /**
     * 维护一个数字，s中的字符添加，t中的字符删除，判断数字是否为0，不为0则跳出
     * @param s
     * @param t
     * @return
     */
    public char findTheDifference(String s, String t) {
        int[] tmp = new int[26];
        if(s.length() == 0) return t.charAt(0);
        for (int i = 0; i < t.length(); i++) {
            if(s.length()!=i){
                tmp[s.charAt(i) - 97]++;
            }
            tmp[t.charAt(i) - 97]--;

        }
        for (int i = 0; i < tmp.length; i++) {
            if(tmp[i]==-1){
                return (char)(i+97);
            }
        }

        return ' ';
    }

    public char tijie(String s, String t){
        int length = s.length();
        char c = t.charAt(length);
        for(int i=0;i<length;i++){
            c ^= s.charAt(i);
            c ^= t.charAt(i);
        }
        return c;
    }

    @Test
    public void test(){
        String a = "";
        String b = "";
        System.out.println(tijie(a, b));
    }
}
